mapper.select:
select count(bbs_no) from bbs




    bbs_title like #{find_name}


    bbs_cont like #{find_name}

select * from (select rowNum rNum, bbs_no, bbs_name, bbs_title, bbs_hit, bbs_ref, bbs_step, bbs_level, bbs_date from (select * from bbs
bbs_title like #{find_name}
bbs_cont like #{find_name}
order by bbs_ref desc, bbs_level asc)) where rNum >= #{startrow} and rNum <= ${endrow}

mapper.update:
update bbs
set
bbs_name = #{bbs_name}, bbs_title = #{bbs_title}, bbs_cont = #{bbs_cont}, bbs_file = #{bbs_file}
where bbs_no = #{bbs_no}
